import './style.css';
import React from "react";
import Cart from './Cart'
import { loadHyper } from "@juspay-tech/hyper-js";
import { Elements, useHyper } from "@juspay-tech/react-hyper-js";
import SDK from './SDK'
import CartItems from './CartItems';
import studioDescription from '../assests/studioDescription.png'
import Confirm from './Confirm';
const stripePromise = loadHyper("pk_snd_1e5425f5dea94ee793cf34ea326294d8");
export default function Workspace({clientSecret}) {
    const stripe = useHyper()
    const [showSDK , setShowSDK] = React.useState(false)
    const [confirmStatus , setStatus] = React.useState("")
    console.log(">>>>",clientSecret)
      const options1 = {
        fields: {
          billingDetails: {
            address: {
              country: "auto",
              city: "auto",
            },
          },
        },
        layout: { 
            type: "accordion",
            defaultCollapsed: false,
            radios: true,
            spacedAccordionItems: true,
        },

        wallets: {
          walletReturnUrl: "https://demo-hyperswitch.netlify.app/",
          applePay: "auto",
          googlePay: "auto",
          style: {
            theme:"dark", 
            type: "default", 
            height: 48
          }
        },
        // paymentMethodOrder: ["cards", "klarna"],
    };
    let options = {
        clientSecret,
        appearance: {
          theme: "charcoal",
          variables: {
            colorPrimary: "#006DF9",
            colorText:"white",
            colorBackground: "transparent",
            spacingUnit: "13px",
          },
          rules: {
            '.Input': {
              color:"#ffffff",
              background: "#000000",
              opacity: "0.25",
              border: "1px solid rgba(250, 250, 250, 0.5)",
              borderRadius: "7px",
            },
            ".Radio": {
              "accentColor":" #621ded",
            },
            '.AccordionItem': {
              color:" rgba(250, 250, 250, 0.5)",
              border:"1px solid transparent",
              background:"#13151B",
            },
            '.AccordionItem--selected': {
              border:"1px solid transparent",
              background:"#13151B",
            },
            ".container .checkmark":{
              border: "2px solid #621ded"
            },
            '.AccordionItem--selected:hover': {
              color: "#FAFAFA"
            },
            '.AccordionItem:hover': {
              color: "#FAFAFA"
            },
            ".CheckboxLabel":{
              color:"rgba(250, 250, 250, 0.5)",
              marginBottom: "3px",
            },
            ".AccordionItemLabel:hover":{
              color: "#FAFAFA"
            },
            ".AccordionItemLabel--selected:hover":{
              color: "#FAFAFA"
            },
            ".AccordionItemLabel--selected":{
              color: "#FAFAFA"
            },
            ".AccordionItemIcon:hover":{
              color: "#FAFAFA"
            },
            ".AccordionItemIcon--selected:hover":{
              color: "#FAFAFA"
            },
            ".AccordionItemIcon--selected":{
              color: "#FAFAFA"
            },
            '.Input:focus':{
              boxShadow: `#621ded4c 1px 0px 0px 5px`,
              border: `1px solid #621ded`,
            },
            '.Label' :{
              color:"rgba(250, 250, 250, 0.5)",
              marginBottom: "3px",
            },
            // See all supported class names and selector syntax below
          },
        },
        fonts: [
          {
            cssSrc:
              "https://fonts.googleapis.com/css2?family=Orbitron:wght@400;500;600;700&display=swap",
          },
          {
            cssSrc:
              "https://fonts.googleapis.com/css2?family=Quicksand:wght@400;500;600;700&family=Qwitcher+Grypen:wght@400;700&display=swap",
          },
          {
            cssSrc: "https://fonts.googleapis.com/css2?family=Combo&display=swap",
          },
          {
            family: "something",
            src: "https://fonts.gstatic.com/s/combo/v21/BXRlvF3Jh_fIhj0lDO5Q82f1.woff2",
            weight: "700",
          },
        ],
        locale: "en",
        loader: "always",
      };
      React.useEffect(() => {
        if (!stripe) {
          return;
        }
        const status = new URLSearchParams(window.location.search).get(
          "status"
        );
        console.log("status ", status)
        if (!status) {
          return;
        } else {
            setStatus(status)
        }
      }, [stripe]);
      var elements = clientSecret && (
        <div className='elements'>
          <Elements options={options} stripe={stripePromise}>
            <SDK options1={options1} layout={options1.layout}/>
          </Elements>
        </div>
      );
      var ele = showSDK && !confirmStatus ? (
        <>
            <div className='body1'>
                {/* <div className='bodyContainer'> */}
                {elements}
                {/* </div> */}
            </div>
            <div className='body2'>
            {/* <div className='bodyContainer2'> */}
                {/* <div className='containerText'>Order Summary</div>
                <CartItems/>
                <div className='paymentDescription'>
                    <div className="textbox">
                    <ul>
                        <li className='listItem'>This is a real transaction, and the money will be auto-refunded to your account in 4-5 days.</li>
                        <li className='listItem'>Select other country to see different currency and other payment options.</li>
                    </ul> 
                    </div>
                </div> */}
                <img src={studioDescription} className="studioDes"/>
            {/* </div>  */}
            </div>
        </>
        ): !showSDK && !confirmStatus 
            ?(<Cart setOpenSDK={setShowSDK}/>)
            : (<Confirm status={confirmStatus}/>)

    return (
    <div className="Container">
        {ele}
    </div>
    )
}
